import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-book-complaint',
  templateUrl: './book-complaint.component.html',
  styleUrls: ['./book-complaint.component.css'],
})
export class BookComplaintComponent {
  complaintForm: FormGroup;
  message: string = '';

  product: string = '';

  constructor(private fb: FormBuilder, private apiService: ApiService) {
    this.complaintForm = this.fb.group({
      complaintName: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(50),
        ],
      ],
      productModelNumber: ['', [Validators.required]],
      clientId: ['', [Validators.required]],
      engineerId: ['', [Validators.required]],
    });
  }

  onSubmit() {
    this.submittedData = this.myForm.value;

    this.service.addData(this.myForm.value).subscribe({
      next: (data) => {
        this.go = true;
        console.log('✅ Data added:', data);
        alert('✅ Data added successfully!');
        this.myForm.reset();
      },
      error: (err) => {
        if (err.status === 409) {
          this.submittedData = null;
          alert('❌ Data with this name already exists');
        } else {
          alert('Something went wrong while adding data!');
        }
      },
    });
  }

  // ngOnInit(): void {
  //   this.route.paramMap.subscribe((params) => {
  //     this.product = params.get('product')!;
  //   });
  // }

  // onSubmit(): void {
  //   if (this.complaintForm.valid) {
  //     const { clientId, productModelNumber, description } =
  //       this.complaintForm.value;
  //     const complaint = { description };
  //     this.apiService
  //       .bookComplaint(clientId, productModelNumber, complaint)
  //       .subscribe(
  //         (response) => {
  //           this.message = response;
  //         },
  //         (error) => {
  //           this.message = 'Failed to book complaint.';
  //         }
  //       );
  //   }
  // }
}
<div class="container">
  <h1 class="banner">Book a complaint</h1>
  <form class="body" [formGroup]="myForm" (ngSubmit)="onSubmit()">
    <!--Complaint Name -->
    <div class="form-group name">
      <label for="complaintName">Complaint Name</label>
      <input
        type="text"
        formControlName="complaintName"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('complaintName')?.invalid &&
            myForm.get('complaintName')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('complaintName')?.touched &&
          myForm.get('complaintName')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('complaintName')?.errors?.['required']"
          >complaint Name is required.</small
        >
        <small *ngIf="myForm.get('complaintName')?.errors?.['minlength']"
          >Minimum 3 characters.</small
        >
        <small *ngIf="myForm.get('complaintName')?.errors?.['maxlength']"
          >Maximum 50 characters.</small
        >
      </div>
    </div>

    <!-- productModelNumber -->
    <div class="form-group age">
      <label for="productModelNumber"> </label>
      <input
        type="text"
        formControlName="productModelNumber"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('productModelNumber')?.invalid &&
            myForm.get('productModelNumber')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('productModelNumber')?.touched &&
          myForm.get('agproductModelNumbere')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('productModelNumber')?.errors?.['required']"
          >productModelNumber is required.</small
        >
      </div>
    </div>

    <!-- ClientId -->
    <div class="form-group salary">
      <label for="clientId">ClientId</label>
      <input
        type="text"
        formControlName="clientId"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('clientId')?.invalid && myForm.get('clientId')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('clientId')?.touched && myForm.get('clientId')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('clientId')?.errors?.['required']"
          >ClientId is required.</small
        >
      </div>
    </div>

    <!-- EngineerId -->
    <div class="form-group salary">
      <label for="engineerId">EngineerId</label>
      <input
        type="text"
        formControlName="engineerId"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('engineerId')?.invalid &&
            myForm.get('engineerId')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('engineerId')?.touched && myForm.get('engineerId')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('engineerId')?.errors?.['required']"
          >EngineerId is required.</small
        >
      </div>
    </div>

    <!-- Submit Button -->
    <button
      class="btn btn-primary mt-3"
      type="submit"
      [disabled]="myForm.invalid"
    >
      Submit
    </button>
  </form>
</div>

