import { Component } from '@angular/core';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-get-complaint-by-client',
  templateUrl: './get-complaint-by-client.component.html',
  styleUrls: ['./get-complaint-by-client.component.css'],
})
export class GetComplaintByClientComponent {
  id: String = '';
  data: Component = {};

  constructor(private service: ApiService) {}

  onSubmit() {
    this.service.getComplaintByClient(this.id).subscribe((data: any) => {
      // console.log(data);
      this.data = data;
      console.log(this.data);
      return this.data;
    });
  }
}
<div class="card-body">
  <form
    id="id-form"
    name="id"
    (ngSubmit)="f.form.valid && onSubmit()"
    action=""
    #f="ngForm"
  >
    <input
      name="input-id"
      [(ngModel)]="id"
      placeholder="Enter ID"
      type="text"
    />
    <button
      name="submit-button"
      type="submit"
      class="btn search btn-primary"
      [disabled]="f.form.invalid"
    >
      Search
    </button>
  </form>
</div>
