import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-book-complaint',
  templateUrl: './book-complaint.component.html',
  styleUrls: ['./book-complaint.component.css'],
})
export class BookComplaintComponent {
  complaintForm: FormGroup;
  message: string = '';

  product: string = '';

  constructor(private fb: FormBuilder, private apiService: ApiService) {
    this.complaintForm = this.fb.group({
      clientId: ['', Validators.required],
      productModelNumber: ['', Validators.required],
      description: ['', Validators.required],
    });
  }

  // ngOnInit(): void {
  //   this.route.paramMap.subscribe((params) => {
  //     this.product = params.get('product')!;
  //   });
  // }

  // onSubmit(): void {
  //   if (this.complaintForm.valid) {
  //     const { clientId, productModelNumber, description } =
  //       this.complaintForm.value;
  //     const complaint = { description };
  //     this.apiService
  //       .bookComplaint(clientId, productModelNumber, complaint)
  //       .subscribe(
  //         (response) => {
  //           this.message = response;
  //         },
  //         (error) => {
  //           this.message = 'Failed to book complaint.';
  //         }
  //       );
  //   }
  // }
}
<div class="container">
  <h2>Book Complaint</h2>
  <form [formGroup]="complaintForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="clientId">Client ID:</label>
      <input type="text" id="clientId" formControlName="clientId" required />
      <div
        *ngIf="
          complaintForm.get('clientId')!.invalid &&
          complaintForm.get('clientId')!.touched
        "
      >
        <small class="error">Client ID is required.</small>
      </div>
    </div>
    <div class="form-group">
      <label for="productModelNumber">Product Model Number:</label>
      <input
        type="text"
        id="productModelNumber"
        formControlName="productModelNumber"
        required
      />
      <div
        *ngIf="
          complaintForm.get('productModelNumber')!.invalid &&
          complaintForm.get('productModelNumber')!.touched
        "
      >
        <small class="error">Product Model Number is required.</small>
      </div>
    </div>
    <div class="form-group">
      <label for="description">Complaint:</label>
      <textarea
        id="description"
        formControlName="description"
        required
      ></textarea>
      <div
        *ngIf="
          complaintForm.get('description')!.invalid &&
          complaintForm.get('description')!.touched
        "
      >
        <small class="error">Complaint description is required.</small>
      </div>
    </div>
    <button type="submit" [disabled]="complaintForm.invalid">Submit</button>
  </form>
  <p>{{ message }}</p>
</div>
