import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css'],
})
export class SignUpComponent {
  myForm!: FormGroup;
  submitted = false;
  successMessage = '';
  errorMessage = '';

  constructor(private fb: FormBuilder, private apiService: ApiService) {}
  ngOnInit(): void {
    this.myForm = this.fb.group({
      clientId: ['', [Validators.required, Validators.minLength(3)]],
      clientName: ['', [Validators.required, Validators.minLength(3)]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      address: ['', [Validators.required]],
      phoneNumber: [
        '',
        [Validators.required, Validators.pattern(/^[0-9]{10}$/)],
      ],
    });
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.myForm.invalid) {
      return;
    }

    const clientData = this.myForm.value;

    this.apiService.registerClient(clientData).subscribe({
      next: (response) => {
        this.successMessage = 'Client registered successfully!';
        this.errorMessage = '';
        this.myForm.reset();
        this.submitted = false;
      },
      error: (error) => {
        this.errorMessage = 'Registration failed. Please try again.';
        this.successMessage = '';
        console.error(error);
      },
    });
  }
}
<div class="main">
  <div class="left">
    <h1 class="banner1">Regester</h1>
    <h1 class="banner2">to add Data</h1>
  </div>
  <div class="right">
    <form class="body" [formGroup]="myForm" (ngSubmit)="onSubmit()">
      <div class="form-group name">
        <label for="name">Name</label>
        <input
          type="text"
          formControlName="name"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              myForm.get('name')?.invalid && myForm.get('name')?.touched
          }"
        />
        <div
          *ngIf="myForm.get('name')?.touched && myForm.get('name')?.invalid"
          class="text-danger"
        >
          <small *ngIf="myForm.get('name')?.errors?.['required']"
            >Name is required.</small
          >
          <small *ngIf="myForm.get('name')?.errors?.['minlength']"
            >Minimum 3 characters.</small
          >
          <small *ngIf="myForm.get('name')?.errors?.['maxlength']"
            >Maximum 50 characters.</small
          >
        </div>
      </div>

      <div class="form-group password">
        <label for="password">password</label>
        <input
          type="password"
          formControlName="password"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              myForm.get('password')?.invalid && myForm.get('password')?.touched
          }"
        />
        <div
          *ngIf="
            myForm.get('password')?.touched && myForm.get('password')?.invalid
          "
          class="text-danger"
        >
          <small *ngIf="myForm.get('password')?.errors?.['required']"
            >password is required.</small
          >
          <small *ngIf="myForm.get('password')?.errors?.['minlength']"
            >Minimum 8 characters.</small
          >
          <small *ngIf="myForm.get('password')?.errors?.['maxlength']"
            >Maximum 50 characters.</small
          >
        </div>
      </div>

      <div class="form-group salary">
        <label for="phone_number">Phone Number</label>
        <input
          type="number"
          formControlName="phone_number"
          class="form-control"
          [ngClass]="{
            'is-invalid':
              myForm.get('phone_number')?.invalid &&
              myForm.get('phone_number')?.touched
          }"
        />
        <div
          *ngIf="
            myForm.get('phone_number')?.touched &&
            myForm.get('phone_number')?.invalid
          "
          class="text-danger"
        >
          <small *ngIf="myForm.get('phone_number')?.errors?.['required']"
            >phone_number is required.</small
          >
          <small *ngIf="myForm.get('phone_number')?.errors?.['pattern']">
            Phone number must be exactly 10 digits.
          </small>

          <small *ngIf="myForm.get('phone_number')?.errors?.['pattern']"
            >Only numeric values allowed.</small
          >
        </div>
      </div>

      <button
        class="btn btn-primary mt-3"
        type="submit"
        [disabled]="myForm.invalid"
      >
        Register
      </button>
    </form>
  </div>
</div>
