import { Component } from '@angular/core';
import { LoginService } from '../service/login.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  isLogin: boolean = false;

  constructor(private login: LoginService) {}

  ngOnInit() {
    this.login.isSignedIn$.subscribe((status) => {
      this.isLogin = status;
    });
  }
}
<nav class="navbar">
  <h3 class="banner logo">Abc</h3>
  <ul class="nav-links">
    <li><a class="navlink" routerLink="">Home</a></li>
    <li>
      <a class="navlink" routerLink="/addData">View My Complaints</a>
    </li>
    <li>
      <a class="navlink" routerLink="/getAllData">View All Complaints</a>
    </li>
    <li>
      <a class="navlink" routerLink="/logIn">LogIn</a>
    </li>
    <li class="logOut">
      <a class="navlink">Log Out</a>
    </li>
  </ul>
</nav>
