import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-book-complaint',
  templateUrl: './book-complaint.component.html',
  styleUrls: ['./book-complaint.component.css'],
})
export class BookComplaintComponent {
  complaintForm: FormGroup;
  message: string = '';
  clientId: string = 'client001';
  product: string = '';

  constructor(
    private fb: FormBuilder,
    private apiService: ApiService,
    private route: ActivatedRoute
  ) {
    this.complaintForm = this.fb.group({
      complaintName: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(50),
        ],
      ],
      productModelNumber: [
        { value: '', disabled: true },
        [Validators.required],
      ],
      clientId: [{ value: '', disabled: true }, [Validators.required]],
      // engineerId: ['', [Validators.required]],
    });
  }

  onSubmit() {
    this.apiService.addComplaint(this.complaintForm.value).subscribe({
      next: (data) => {
        console.log('✅ Data added:', data);
        alert('✅ Data added successfully!');
        this.complaintForm.reset();
      },
    });
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe((params) => {
      this.product = params.get('modelNumber')!;
      this.complaintForm.get('productModelNumber')?.setValue(this.product);
      this.complaintForm.get('clientId')?.setValue(this.clientId);
    });
  }

  // onSubmit(): void {
  //   if (this.complaintForm.valid) {
  //     const { clientId, productModelNumber, description } =
  //       this.complaintForm.value;
  //     const complaint = { description };
  //     this.apiService
  //       .bookComplaint(clientId, productModelNumber, complaint)
  //       .subscribe(
  //         (response) => {
  //           this.message = response;
  //         },
  //         (error) => {
  //           this.message = 'Failed to book complaint.';
  //         }
  //       );
  //   }
  // }
}
<div class="container">
  <h1 class="banner">Book a complaint</h1>
  <form class="body" [formGroup]="complaintForm" (ngSubmit)="onSubmit()">
    <!--Complaint Name -->
    <div class="form-group name">
      <label for="complaintName">Complaint Name</label>
      <input
        type="text"
        formControlName="complaintName"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            complaintForm.get('complaintName')?.invalid &&
            complaintForm.get('complaintName')?.touched
        }"
      />
      <div
        *ngIf="
          complaintForm.get('complaintName')?.touched &&
          complaintForm.get('complaintName')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="complaintForm.get('complaintName')?.errors?.['required']"
          >complaint Name is required.</small
        >
        <small *ngIf="complaintForm.get('complaintName')?.errors?.['minlength']"
          >Minimum 3 characters.</small
        >
        <small *ngIf="complaintForm.get('complaintName')?.errors?.['maxlength']"
          >Maximum 50 characters.</small
        >
      </div>
    </div>

    <!-- productModelNumber -->
    <div class="form-group age">
      <label for="productModelNumber"> productModelNumber</label>
      <input
        type="text"
        formControlName="productModelNumber"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            complaintForm.get('productModelNumber')?.invalid &&
            complaintForm.get('productModelNumber')?.touched
        }"
      />
      <div
        *ngIf="
          complaintForm.get('productModelNumber')?.touched &&
          complaintForm.get('agproductModelNumbere')?.invalid
        "
        class="text-danger"
      >
        <small
          *ngIf="complaintForm.get('productModelNumber')?.errors?.['required']"
          >productModelNumber is required.</small
        >
      </div>
    </div>

    <!-- ClientId -->
    <div class="form-group salary">
      <label for="clientId">ClientId</label>
      <input
        type="text"
        formControlName="clientId"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            complaintForm.get('clientId')?.invalid &&
            complaintForm.get('clientId')?.touched
        }"
      />
      <div
        *ngIf="
          complaintForm.get('clientId')?.touched &&
          complaintForm.get('clientId')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="complaintForm.get('clientId')?.errors?.['required']"
          >ClientId is required.</small
        >
      </div>
    </div>

    <!-- EngineerId -->
    <div class="form-group salary">
      <label for="engineerId">EngineerId</label>
      <input
        type="text"
        formControlName="engineerId"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            complaintForm.get('engineerId')?.invalid &&
            complaintForm.get('engineerId')?.touched
        }"
      />
      <div
        *ngIf="
          complaintForm.get('engineerId')?.touched &&
          complaintForm.get('engineerId')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="complaintForm.get('engineerId')?.errors?.['required']"
          >EngineerId is required.</small
        >
      </div>
    </div>

    <!-- Submit Button -->
    <button
      class="btn btn-primary mt-3"
      type="submit"
      [disabled]="complaintForm.invalid"
    >
      Submit
    </button>
  </form>
</div>

