import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css'],
})
export class SignUpComponent {
  myForm!: FormGroup;
  submitted = false;
  successMessage = '';
  errorMessage = '';

  constructor(private fb: FormBuilder, private apiService: ApiService) {}
  ngOnInit(): void {
    this.myForm = this.fb.group({
      clientId: ['', [Validators.required, Validators.minLength(3)]],
      clientName: ['', [Validators.required, Validators.minLength(3)]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      address: ['', [Validators.required]],
      phoneNumber: [
        '',
        [Validators.required, Validators.pattern(/^[0-9]{10}$/)],
      ],
    });
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.myForm.invalid) {
      return;
    }

    const clientData = this.myForm.value;

    this.apiService.registerClient(clientData).subscribe({
      next: (response) => {
        this.successMessage = 'Client registered successfully!';
        this.errorMessage = '';
        this.myForm.reset();
        this.submitted = false;
      },
      error: (error) => {
        this.errorMessage = 'Registration failed. Please try again.';
        this.successMessage = '';
        console.error(error);
      },
    });
  }
}
<form [formGroup]="myForm" (ngSubmit)="onSubmit()">
  <h2>Client Registration</h2>

  <!-- Client ID -->
  <div class="form-group">
    <label for="clientId">Client ID</label>
    <input type="text" formControlName="clientId" />
    <div class="error" *ngIf="submitted && myForm.get('clientId')?.invalid">
      <small *ngIf="myForm.get('clientId')?.errors?.['required']"
        >Client ID is required.</small
      >
      <small *ngIf="myForm.get('clientId')?.errors?.['minlength']"
        >Minimum 3 characters.</small
      >
      <small *ngIf="myForm.get('clientId')?.errors?.['pattern']"
        >Only alphanumeric characters allowed.</small
      >
    </div>
  </div>

  <!-- Client Name -->
  <div class="form-group">
    <label for="clientName">Client Name</label>
    <input type="text" formControlName="clientName" />
    <div class="error" *ngIf="submitted && myForm.get('clientName')?.invalid">
      <small *ngIf="myForm.get('clientName')?.errors?.['required']"
        >Name is required.</small
      >
      <small *ngIf="myForm.get('clientName')?.errors?.['minlength']"
        >Minimum 3 characters.</small
      >
      <small *ngIf="myForm.get('clientName')?.errors?.['pattern']"
        >Only letters and spaces allowed.</small
      >
    </div>
  </div>

  <!-- Password -->
  <div class="form-group">
    <label for="password">Password</label>
    <input type="password" formControlName="password" />
    <div class="error" *ngIf="submitted && myForm.get('password')?.invalid">
      <small *ngIf="myForm.get('password')?.errors?.['required']"
        >Password is required.</small
      >
      <small *ngIf="myForm.get('password')?.errors?.['minlength']"
        >Minimum 6 characters.</small
      >
      <small *ngIf="myForm.get('password')?.errors?.['pattern']">
        Must include uppercase, lowercase, number, and special character.
      </small>
    </div>
  </div>

  <!-- Address -->
  <div class="form-group">
    <label for="address">Address</label>
    <input type="text" formControlName="address" />
    <div class="error" *ngIf="submitted && myForm.get('address')?.invalid">
      <small *ngIf="myForm.get('address')?.errors?.['required']"
        >Address is required.</small
      >
      <small *ngIf="myForm.get('address')?.errors?.['minlength']"
        >Minimum 5 characters.</small
      >
    </div>
  </div>

  <!-- Phone Number -->
  <div class="form-group">
    <label for="phoneNumber">Phone Number</label>
    <input type="text" formControlName="phoneNumber" />
    <div class="error" *ngIf="submitted && myForm.get('phoneNumber')?.invalid">
      <small *ngIf="myForm.get('phoneNumber')?.errors?.['required']"
        >Phone number is required.</small
      >
      <small *ngIf="myForm.get('phoneNumber')?.errors?.['pattern']"
        >Must be a 10-digit number.</small
      >
    </div>
  </div>

  <button type="submit" [disabled]="myForm.invalid">Register</button>

  <div class="success" *ngIf="successMessage">{{ successMessage }}</div>
  <div class="error" *ngIf="errorMessage">{{ errorMessage }}</div>
</form>
