import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from 'src/app/service/Api.service';

@Component({
  selector: 'app-book-complaint',
  templateUrl: './book-complaint.component.html',
  styleUrls: ['./book-complaint.component.css'],
})
export class BookComplaintComponent {
  complaintForm: FormGroup;
  message: string = '';

  product: string = '';

  constructor(private fb: FormBuilder, private apiService: ApiService) {
    this.complaintForm = this.fb.group({
      clientId: ['', Validators.required],
      productModelNumber: ['', Validators.required],
      description: ['', Validators.required],
    });
  }

  // ngOnInit(): void {
  //   this.route.paramMap.subscribe((params) => {
  //     this.product = params.get('product')!;
  //   });
  // }

  // onSubmit(): void {
  //   if (this.complaintForm.valid) {
  //     const { clientId, productModelNumber, description } =
  //       this.complaintForm.value;
  //     const complaint = { description };
  //     this.apiService
  //       .bookComplaint(clientId, productModelNumber, complaint)
  //       .subscribe(
  //         (response) => {
  //           this.message = response;
  //         },
  //         (error) => {
  //           this.message = 'Failed to book complaint.';
  //         }
  //       );
  //   }
  // }
}
<div class="container">
  <h1 class="banner">Book a complaint</h1>
  <form class="body" [formGroup]="myForm" (ngSubmit)="onSubmit()">
    <!--Complaint Name -->
    <div class="form-group name">
      <label for="complaint name">Complaint Name</label>
      <input
        type="text"
        formControlName="complaintName"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('complaintName')?.invalid &&
            myForm.get('complaintName')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('complaintName')?.touched &&
          myForm.get('complaintName')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('complaintName')?.errors?.['required']"
          >complaint Name is required.</small
        >
        <small *ngIf="myForm.get('complaintName')?.errors?.['minlength']"
          >Minimum 3 characters.</small
        >
        <small *ngIf="myForm.get('complaintName')?.errors?.['maxlength']"
          >Maximum 50 characters.</small
        >
      </div>
    </div>

    <!-- Age -->
    <div class="form-group age">
      <label for="age">Age</label>
      <input
        type="text"
        formControlName="age"
        class="form-control"
        [ngClass]="{
          'is-invalid': myForm.get('age')?.invalid && myForm.get('age')?.touched
        }"
      />
      <div
        *ngIf="myForm.get('age')?.touched && myForm.get('age')?.invalid"
        class="text-danger"
      >
        <small *ngIf="myForm.get('age')?.errors?.['required']"
          >Age is required.</small
        >
        <small *ngIf="myForm.get('age')?.errors?.['min']"
          >Minimum age is 18.</small
        >
        <small *ngIf="myForm.get('age')?.errors?.['max']"
          >Maximum age is 65.</small
        >
      </div>
    </div>

    <!-- Salary -->
    <div class="form-group salary">
      <label for="salary">Salary</label>
      <input
        type="text"
        formControlName="salary"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('salary')?.invalid && myForm.get('salary')?.touched
        }"
      />
      <div
        *ngIf="myForm.get('salary')?.touched && myForm.get('salary')?.invalid"
        class="text-danger"
      >
        <small *ngIf="myForm.get('salary')?.errors?.['required']"
          >Salary is required.</small
        >
        <small *ngIf="myForm.get('salary')?.errors?.['min']"
          >Minimum salary is 30000.</small
        >
        <small *ngIf="myForm.get('salary')?.errors?.['pattern']"
          >Only numeric values allowed.</small
        >
      </div>
    </div>

    <!-- Department -->
    <div class="form-group department">
      <label for="department">Department</label>
      <input
        type="text"
        formControlName="department"
        class="form-control"
        [ngClass]="{
          'is-invalid':
            myForm.get('department')?.invalid &&
            myForm.get('department')?.touched
        }"
      />
      <div
        *ngIf="
          myForm.get('department')?.touched && myForm.get('department')?.invalid
        "
        class="text-danger"
      >
        <small *ngIf="myForm.get('department')?.errors?.['required']"
          >Department is required.</small
        >
        <small *ngIf="myForm.get('department')?.errors?.['maxlength']"
          >Max 30 characters allowed.</small
        >
      </div>
    </div>

    <!-- Submit Button -->
    <button
      class="btn btn-primary mt-3"
      type="submit"
      [disabled]="myForm.invalid"
    >
      Submit
    </button>
  </form>
</div>

